        /// <summary>
        ///  saan has to improve this function to capture all other informations for the compositions constructions
        /// </summary>
        /// <param name="bengaliText"></param>
        /// <param name="ruleList"></param>
        /// <returns></returns>
        public static string ConvertBengaliToAscii__ByRuleList_AndUpdateStats(string bengaliText, List<BengaliAsciiTransliterator.BengaliAsciiTransliterationRule> ruleList)
        {
            if (string.IsNullOrWhiteSpace(bengaliText))
                return bengaliText;
            var sortedRules = ruleList
                .OrderByDescending(rule => rule.AsciiSyllable?.Length ?? 0)
                .ThenByDescending(rule => rule.BengaliUnicodeSyllable?.Length ?? 0)
                .ThenByDescending(rule => rule.BengaliToAsciiComplexity)
                .ThenByDescending(rule => rule.AsciiToBengaliComplexity)
                .ToList();
            foreach (var rule in sortedRules)
            {
                if (string.IsNullOrWhiteSpace(rule.BengaliUnicodeSyllable))
                    continue;
                string pattern = Regex.Escape(rule.BengaliUnicodeSyllable);
                bengaliText = Regex.Replace(bengaliText, pattern, match =>
                {
                    rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION++;
                    int index = match.Index;
                    // Left context
                    bool isWordStart = index == 0 || bengaliText[index - 1] == ' ';
                    bool isSentenceStart = index == 0 || ".!?\n\r".Contains(bengaliText[index - 1]);
                    // Right context
                    int after = index + match.Length;
                    bool isWordEnd = after >= bengaliText.Length || bengaliText[after] == ' ';
                    bool isSentenceEnd = after >= bengaliText.Length || ".!?\n\r".Contains(bengaliText[after]);
                    if (isWordStart)
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_WORDS_STARTING_JUST_AFTER_SPACE_CHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION++;
                    if (isWordEnd)
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_WORDS_COMPLETING_JUST_BEFORE_SPACE_CHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION++;
                    if (isSentenceStart)
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_SENTENCE_STARTER_JUST_AFTER_DARI_SYMBOLS_OR_FULLSTOP_SYMBOLS_OR_JUST_AFTER_NEWLINES_OR_JUST_AFTER_LINES_FEEDS_OR_JUST_AFTER_PAGE_CHANGES_KUSTCHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION++;
                    if (isSentenceEnd)
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_SENTENCE_COMPLETIONS_JUST_BEFORE_DARI_SYMBOLS_OR_FULLSTOP_SYMBOLS_OR_JUST_BEFORE_NEWLINES_OR_JUST_BEFORE_LINES_FEEDS_OR_JUST_BEFORE_PAGE_CHANGES_KUSTCHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION++;
                    return rule.AsciiSyllable;
                }, RegexOptions.CultureInvariant);
            }
            return bengaliText;
        }// public static string ConvertBengaliToAscii__ByRuleList_AndUpdateStats(string bengaliText, List<BengaliAsciiTransliterationRule> ruleList)
        public static void LogStatisticsReport___From_RuleList(List<BengaliAsciiTransliterator.BengaliAsciiTransliterationRule> RuleList, string savePath)
        {
            using (System.IO.StreamWriter writer = new System.IO.StreamWriter(savePath, false, System.Text.Encoding.UTF8))
            {
                writer.WriteLine("BengaliSyllable,AsciiSyllable,PronunciabilityComplexity,BreathingComplexity,TotalFreq,WordStartFreq,WordEndFreq,SentenceStartFreq,SentenceEndFreq");
                foreach (var rule in RuleList)
                {
                    writer.WriteLine(string.Join(",",
                        EscapeCsv(rule.BengaliUnicodeSyllable),
                        EscapeCsv(rule.AsciiSyllable),
                        rule.DOUBLE_TYPE_PRONUNCIABILITY_COMPLEXITY___TO_ALLOW_OR_DISALLOW_PERCUSSIONS_INSTRUMENTS_OR_OTHER_MELODY_INSTRUMENTS_TO_PLAY_ALONG_WITH_THIS_SYLLABLE_OR_NOT.ToString("F6"),
                        rule.DOUBLE_TYPE_MINIMUM_REMAINING_BREATHING_DURATION_NECESSARY_TO_PRONUNCIATE_THIS_SYLLABLES__COMPLEXITY.ToString("F6"),
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION,
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_WORDS_STARTING_JUST_AFTER_SPACE_CHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION,
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_WORDS_COMPLETING_JUST_BEFORE_SPACE_CHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION,
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_SENTENCE_STARTER_JUST_AFTER_DARI_SYMBOLS_OR_FULLSTOP_SYMBOLS_OR_JUST_AFTER_NEWLINES_OR_JUST_AFTER_LINES_FEEDS_OR_JUST_AFTER_PAGE_CHANGES_KUSTCHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION,
                        rule.STATISTICAL_FREQUENCY_THAT_IS_COUNT_OF_CURRENT_SYLLABLE_USED_IN_SENTENCE_COMPLETIONS_JUST_BEFORE_DARI_SYMBOLS_OR_FULLSTOP_SYMBOLS_OR_JUST_BEFORE_NEWLINES_OR_JUST_BEFORE_LINES_FEEDS_OR_JUST_BEFORE_PAGE_CHANGES_KUSTCHARACTER_IN_WHOLE_TEXT_OR_LYRIC_TEXT_WHILE_DOING_TRANSLITERATION
                    ));
                }
            }
        }// public static void LogStatisticsReport___From_RuleList(List<BengaliAsciiTransliterationRule> RuleList, string savePath)
        private static string EscapeCsv(string value)
        {
            if (string.IsNullOrWhiteSpace(value)) return "\"\"";
            if (value.Contains(",") || value.Contains("\""))
            {
                value = value.Replace("\"", "\"\"");
                return $"\"{value}\"";
            }
            return value;
        }// private static string EscapeCsv(string value)
