class TangentAnimation_SINCOS_TANGENTSINTERSECTIONSGETCENTERS_PROJECTIONS_WAVESWITHTANGENTS_UPDATING(Scene):
    def construct(self):
        ax = Axes()
        sine = ax.plot(np.sin, color=RED)
        coses = ax.plot(np.cos, color=BLUE)
        alpha = ValueTracker(0)
        pointsin = always_redraw(
            lambda: Dot(
                sine.point_from_proportion(alpha.get_value()),
                color=BLUE
            )
        )
        pointcos = always_redraw(
            lambda: Dot(
                coses.point_from_proportion(alpha.get_value()),
                color=BLUE
            )
        )
        tangentsinces = always_redraw(
            lambda: TangentLine(
                sine,
                alpha=alpha.get_value(),
                color=YELLOW,
                length=20
            )
        )
        tangentcoses = always_redraw(
            lambda: TangentLine(
                coses,
                alpha=alpha.get_value(),
                color=GREEN,
                length=20
            )
        )
        intersection = always_redraw(
            lambda: self.get_intersection(tangentsinces, tangentcoses)
        )
        projection_sine = always_redraw(
            lambda: self.project_point(intersection.get_center(), sine)
        )
        projection_cosine = always_redraw(
            lambda: self.project_point(intersection.get_center(), coses)
        )
        line_segment = always_redraw(
            lambda: Line(projection_sine.get_center(), projection_cosine.get_center(), color=WHITE)
        )
        self.add(ax, sine, coses, pointsin, pointcos, tangentsinces, tangentcoses, intersection, projection_sine, projection_cosine, line_segment)
        self.play(alpha.animate.set_value(1), rate_func=linear, run_time=22)
    def get_intersection(self, line1, line2):
        intersection_point = line_intersection(line1.get_start_and_end(), line2.get_start_and_end())
        return Dot(intersection_point, color=PURPLE)
    def project_point(self, point, curve):
        x = point[0]
        y = curve.underlying_function(x)
        return Dot(np.array([x, y, 0]), color=ORANGE)
def line_intersection(line1_points, line2_points):
    xdiff = (line1_points[0][0] - line1_points[1][0], line2_points[0][0] - line2_points[1][0])
    ydiff = (line1_points[0][1] - line1_points[1][1], line2_points[0][1] - line2_points[1][1])
    def det(a, b):
        return a[0] * b[1] - a[1] * b[0]
    div = det(xdiff, ydiff)
    if div == 0:
       raise Exception('lines do not intersect')
    d = (det(*line1_points), det(*line2_points))
    x = det(d, xdiff) / div
    y = det(d, ydiff) / div
    return np.array([x, y, 0])	
