def line_intersection(line1_points, line2_points):
    xdiff = (line1_points[0][0] - line1_points[1][0], line2_points[0][0] - line2_points[1][0])
    ydiff = (line1_points[0][1] - line1_points[1][1], line2_points[0][1] - line2_points[1][1])
    def det(a, b):
        return a[0] * b[1] - a[1] * b[0]
    div = det(xdiff, ydiff)
    if div == 0:
       raise Exception('lines do not intersect')
    d = (det(*line1_points), det(*line2_points))
    x = det(d, xdiff) / div
    y = det(d, ydiff) / div
    return np.array([x, y, 0])		
