from manim import *
class TangentAnimation_SANJOYNATHTHEOREMSSEARCHES_2(Scene):
    def construct(self):
        ax = Axes(x_range=[-30, 30], axis_config={"include_numbers": True})  # Set the x-axis range from -30 to +30 and include coordinates at the axis marker points
        sine = ax.plot(np.sin, color=RED)
        coses = ax.plot(np.cos, color=BLUE)
        alpha = ValueTracker(0)
        pointsin = always_redraw(
            lambda: Dot(
                sine.point_from_proportion(alpha.get_value()),
                color=BLUE
            )
        )
        pointcos = always_redraw(
            lambda: Dot(
                coses.point_from_proportion(alpha.get_value()),
                color=BLUE
            )
        )
        tangentsinces = always_redraw(
            lambda: TangentLine(
                sine,
                alpha=alpha.get_value(),
                color=YELLOW,
                length=20
            ).set_name("LTANGSSIN to RTANGSSIN")  # Name the TangentLine of the sine curve as LTANGSSIN to RTANGSSIN
        )
        tangentcoses = always_redraw(
            lambda: TangentLine(
                coses,
                alpha=alpha.get_value(),
                color=GREEN,
                length=20
            ).set_name("LTANGSCOS to RTANGSCOS")  # Name the TangentLine of the cosine curve as LTANGSCOS to RTANGSCOS
        )
        intersection = always_redraw(
            lambda: self.get_intersection(tangentsinces, tangentcoses).set_name("INTERSECT")  # Name the intersection dot as INTERSECT
        )
        projection_sine = always_redraw(
            lambda: self.project_point(intersection.get_center()[0], sine).set_name("INTERSECT_PROJ_ON_SIN_CURVE")  # Name the projection dot as INTERSECT_PROJ_ON_SIN_CURVE
        )
        projection_cosine = always_redraw(
            lambda: self.project_point(intersection.get_center()[0], coses).set_name("INTERSECT_PROJ_ON_COS_CURVE")  # Name the projection dot as INTERSECT_PROJ_ON_COS_CURVE
        )
        line_segment = always_redraw(
            lambda: Line(projection_sine.get_center(), projection_cosine.get_center(), color=WHITE).set_name("SEGPROJS_CONNECT")  # Connect the projection dots with a line named SEGPROJS_CONNECT
        )
        # Create a separate curve to trace the length of the line segment
        length_trace = always_redraw(
            lambda: self.create_length_trace(line_segment)
        )
        # Create a separate circle with the center as the midpoint of the line segment and the line segment as the diameter
        circle = always_redraw(
            lambda: self.create_circle(line_segment)
        )
        # Create a text at the top that fits within the screen width
        text = Text("SANJOY NATH MANIMATIONS QHENOMENOLOGY").scale_to_fit_width(config.frame_width - 1).to_edge(UP)
        # Draw the convex quadrilateral with the 4 points LTANGSSIN, RTANGSSIN, LTANGSCOS, RTANGSCOS and find its area
        quadrilateral = always_redraw(
            lambda: Polygon(tangentsinces.get_start(), tangentsinces.get_end(), tangentcoses.get_start(), tangentcoses.get_end(), color=PURPLE)
        )
        area_text = always_redraw(
            lambda: Text(f"Area: {self.calculate_area(quadrilateral)}").next_to(text, DOWN)
        )
        self.add(ax, sine, coses, pointsin, pointcos, tangentsinces, tangentcoses, intersection, projection_sine, projection_cosine, line_segment, length_trace, circle, text, quadrilateral, area_text)
        # Run the animation 6 times instead of infinitely
        for _ in range(6):
            self.play(alpha.animate.set_value(1), rate_func=linear, run_time=22)
            alpha.set_value(0)
    def get_intersection(self, line1, line2):
        intersection_point = line_intersection(line1.get_start_and_end(), line2.get_start_and_end())
        return Dot(intersection_point, color=PURPLE)
    def project_point(self, x_value, curve):
        y_value = curve.underlying_function(x_value)
        return Dot(np.array([x_value, y_value, 0]), color=ORANGE)
    def create_length_trace(self, line_segment):
        length = line_segment.get_length()
        return ParametricFunction(lambda t: np.array([t, length * np.sin(t), 0]), t_range=np.array([0, TAU]), color=PINK)
    def create_circle(self, line_segment):
        midpoint = line_segment.get_center()
        radius = line_segment.get_length() / 2
        return Circle(radius=radius).move_to(midpoint)
    def calculate_area(self, polygon):
        vertices = polygon.get_vertices()
        area = 0.0
        for i in range(len(vertices)):
            x1, y1 = vertices[i][:2]
            x2, y2 = vertices[(i + 1) % len(vertices)][:2]
            area += x1 * y2 - y1 * x2
        return abs(area) / 2
def line_intersection(line1_points, line2_points):
    xdiff = (line1_points[0][0] - line1_points[1][0], line2_points[0][0] - line2_points[1][0])
    ydiff = (line1_points[0][1] - line1_points[1][1], line2_points[0][1] - line2_points[1][1])
    def det(a, b):
        return a[0] * b[1] - a[1] * b[0]
    div = det(xdiff, ydiff)
    if div == 0:
       raise Exception('lines do not intersect')
    d = (det(*line1_points), det(*line2_points))
    x = det(d, xdiff) / div
    y = det(d, ydiff) / div
    return np.array([x, y, 0])					
