class ImportNetworkxGraph___WithQhenomenology__30secs_3times_blinkingnodesedges(Scene):
    def construct(self):
        # Create a text at the top that fits within the screen width
        text = Text("SANJOY NATH MANIMATIONS QHENOMENOLOGY GeometrifyingTrigonometry BOLLS Bunch Of Line segments").scale_to_fit_width(config.frame_width - 1).to_edge(UP)
        self.add(text)  # Ensure the text remains on the screen
        # Create a graph from the NetworkX graph
        G = Graph.from_networkx(nxgraph, layout="spring", layout_scale=3.5, labels=True)
        # Function to blink nodes and edges
        def blink_labels(graph):
            animations = []
            for node in graph.vertices:
                animations.append(Indicate(graph[node], color=YELLOW, scale_factor=1.2))
            for edge in graph.edges:
                animations.append(Indicate(graph.edges[edge], color=RED, scale_factor=1.2))
            return animations
        # Play the animation three times
        for _ in range(3):
            # Animate the creation of the graph
            self.play(Create(G), run_time=10)
            # Animate the movement of the graph vertices
            self.play(*[G[v].animate.move_to(5 * RIGHT * np.cos(ind / 7 * PI) +
                                             3 * UP * np.sin(ind / 7 * PI))
                        for ind, v in enumerate(G.vertices)], run_time=10)
            # Blink the labels on nodes and edges
            self.play(*blink_labels(G), run_time=10)
            # Animate the uncreation of the graph
            self.play(Uncreate(G), run_time=10)
