import csv
class LogScalingExample___withdatadumps(Scene):
    def construct(self):
        ax = Axes(
            x_range=[0, 10, 1],
            y_range=[-2, 6, 1],
            tips=False,
            axis_config={"include_numbers": True},
            y_axis_config={"scaling": LogBase(custom_labels=True)},
        )
        # x_min must be > 0 because log is undefined at 0.
        graph = ax.plot(lambda x: x ** 2, x_range=[0.001, 10], use_smoothing=False)
        self.add(ax, graph)
        # Extract data points from the graph
        x_values = [x for x in range(1, 11)]
        y_values = [x ** 2 for x in x_values]
        # Write data to CSV file
        with open('graph_data.csv', 'w', newline='') as csvfile:
            fieldnames = ['x', 'y']
            writer = csv.DictWriter(csvfile, fieldnames=fieldnames)
            writer.writeheader()
            for x, y in zip(x_values, y_values):
                writer.writerow({'x': x, 'y': y})
# Create an instance of the scene to trigger the construct method
